import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Link, Route, Routes } from "react-router-dom";
import { Navigation, Footer, Home, Contact, SignUp, SignIn, Doctprofile, Logout } from "./components";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isauthenticated: false
    }
    this.getuser();
  }

  getuser() {
    axios.get("http://localhost:4000/signin", { withCredentials: true })
      .then(res => this.setState({ isauthenticated: res.data.isauthenticated }))
      .catch(error => console.log(error));
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.isauthenticated !== this.state.isauthenticated) {
      this.render();
    }
  }

  //#region  Routers
  RouterHome() {
    if (true) {
      return (
        <Route exact path="/" element={<Home />} />
      )
    }
  }
  RouterLogout() {
    if (this.state.isauthenticated) {
      return (
        <Route exact path="/logout" element={<Logout isauthenticated={this.state.isauthenticated} />} />
      )
    }
    else {
      return (
        <Route exact path="/logout" element={<Home />} />
      )
    }
  }
  RouterSignUp() {
    if (!this.state.isauthenticated) {
      return (
        <Route path="/signup" element={<SignUp />} />
      )
    }
    else {
      return (
        null
      )
    }
  }
  RouterSignIn() {
    if (!this.state.isauthenticated) {
      return (
        <Route path="/signin" element={<SignIn />} />
      )
    }
    else {
      return (
        null
      )
    }
  }
  RouterContact() {
    if (true) {
      return (
        <Route path="/contact" element={<Contact title="Contact" />} />
      )
    }
  }
  RouterDoctprofile() {
    if (this.state.isauthenticated) {
      return (
        <Route path="/doctorNotAPP" element={<Doctprofile />} />
      )
    }
    else {
      return (
        <Route path="/doctorNotAPP" element={<SignIn />} />
      )
    }
  }
  //#endregion

  render() {
    return (
      <div className="App">
        <Router>
          <Navigation />
          <Routes>
            {this.RouterHome()}
            {this.RouterSignUp()}
            {this.RouterContact()}
            {this.RouterDoctprofile()}
            {this.RouterSignIn()}
            {this.RouterLogout()}
          </Routes>
        </Router>
      </div>
    );
  }
}
export default App;
