import React from "react";
// import './sign.css';
class SignIn extends React.Component {
    constructor() {
        super();
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            name: '',
            password: '',
            errorSingin:[]
        }
    }

    onSubmit(e) {
        e.preventDefault();
        const phone = e.target.phone.value;
        const password = e.target.password.value;
        if(isNaN(phone)){
            this.setState({errorSingin:['this is not ']})
        }
        this.render();
    }

    render() {
        if (true) {
            console.log("render");
            return (
                <div class="col-md-4 offset-md-4 con">
                    {this.state.errorSingin.length>=0 ?
                    this.state.errorSingin.map((error)=>(
                        <div class="alert alert-danger" key={Math.random()} role="alert">
                            {error}
                        </div>
                    ))
                        :
                        <div></div>
                    }
                    <h1>Sign In</h1>
                    <form onSubmit={this.onSubmit}>
                        <div class="mb-3">
                            <label for="email" class="form-label">Phone</label>
                            <input type="text" class="form-control" placeholder="Enter Your Phone Or Email" name="phone" minlength="11" maxlength="11" required />
                        </div>
                        <div class="mb-3">
                            <label for="password" class="form-label">Password</label>
                            <input type="password" class="form-control" placeholder="Enter Your Password" name="password" minlength="10" required />
                        </div>
                        <button type="submit" class="btn btn-primary btn1">Sign In</button>
                    </form>
                </div>
            )
        }
        else {
            window.location.assign("/About");
        }
    }
}
export default SignIn;
