import React, { useEffect, useState } from "react"; //to use function to get information  and useState to store data
import './Doctprofile.css';//to css file
import axios from "axios";//to get data API
import { Card, Container, Row, Table, Button } from "react-bootstrap";//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';//bootstrap
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';//bootstrap
import { faPlus } from '@fortawesome/free-solid-svg-icons';//bootstrap
function Doctprofile() {
    const [person, setperson] = useState(Array);//to store data from DataBase NodeJs

    //#region to get data from DataBase NodeJs
    useEffect(() => {
        axios.get("http://localhost:5000/doctor")
            .then(res => {
                setperson(res.data.dr);
            });
    }, [])
    //#endregion
    
    //#region to erase doctor from display
    function deletedoct(e) {
        console.log(e.target.id);
    }
    //#endregion

    //#region  to Use spin fontawesome
    const [showspain, setshowspain] = useState(false);

    function funshowspain() {
        setshowspain(true);
    }
    function funclosespain() {
        setshowspain(false);
    }
    //#endregion

    
    return (
        <Container>
            {person.map((user) => (
                <Row className="row">
                    {user.map((users) => (
                        <Card style={{ width: '25rem', padding: '0px' }}>
                            <Card.Img variant="top" src={process.env.PUBLIC_URL + '/dr1.jpg'} />
                            <Table style={{ margin: '5px' }}>
                                <tbody>
                                    <tr>
                                        <td>Dr.Name</td>
                                        <td>{users.name}</td>
                                    </tr>
                                    <tr>
                                        <td>Dr.Specialised</td>
                                        <td>{users.specialised}</td>
                                    </tr>
                                    <tr>
                                        <td>Dr.Home Work</td>
                                        <td>{users.hourwork}</td>
                                    </tr>
                                </tbody>
                                <Button title="Is You Want To Add Doctor" className="btnadd" id={users._id} onMouseLeave={funclosespain} onMouseOver={funshowspain} onClick={deletedoct} variant="success"> Add <FontAwesomeIcon icon={faPlus} spin={showspain} ></FontAwesomeIcon></Button>
                            </Table>
                        </Card>
                    ))}
                </Row>
            ))}
        </Container>
    );
}


export default Doctprofile;
