import React from "react";
import './signup.css';
import EmailJS from "emailjs-com";
class SignUp extends React.Component {
    constructor() {
        super();
        this.SendEmail = this.SendEmail.bind(this);
    }
    state = {
        fromName: 'Ahmed Galal',
        fromEmail: '',
        emailconfirm:Number
    }
    SendEmail(e) {
        e.preventDefault();
        if (this.state.fromEmail.indexOf('@gmail.com') != -1) {
            const Confirmcode = Math.floor((Math.random() * 1000000000000) + 1000);
            this.setState({emailconfirm:Confirmcode});
            EmailJS.send(
                'service_mz4n60i',
                'template_ucsfkhq',
                this.state,
                'user_Sn6vz41QrsTNh2oLz7PKv'
            ).then(res => console.log(res.text)).catch(error => console.log(error));
        }
        else {
            alert("Please Enter You Email Correct" + "\n" + "E.g:*******@gmail.com");
        }
    }
    onSubmit(e) {
        e.preventDefault();
        console.log(e.target.confirm_email.value);
        console.log(this.state.e);
    }
    render() {
        return (
            < div className="col-md-4 offset-md-4 con" >
                {false ?
                    <div className="alert alert-danger" role="alert">
                    </div> :
                    <div></div>
                }
                <h1>Sign Up</h1>
                <form onSubmit={this.onSubmit}>
                    <div className="mb-3">
                        <label for="phone" className="form-label">Phone</label>
                        <input type="text" className="form-control" name="phone" maxlength="11" minlength="11" required />
                    </div>
                    <div className="mb-3">
                        <label for="name" className="form-label">Full Name</label>
                        <input type="text" className="form-control" name="name" required />
                    </div>
                    <div className="mb-3">
                        <label for="email" className="form-label">Email</label>
                        <input type="email" className="form-control" name="email" id="email" onChange={(e) => this.setState({ fromEmail: e.target.value })} required />
                    </div>
                    <div className="mb-3">
                        <button type="submit" onClick={this.SendEmail} className="btn btn-primary btn1">Confirm</button>
                    </div>
                    <div className="mb-3">
                        <label for="name" className="form-label">Confirm Code Email</label>
                        <input type="text" className="form-control" name="confirm_email" placeholder="Confirm Code Email" required />
                    </div>
                    <div className="mb-3">
                        <label for="password" className="form-label">Password</label>
                        <input type="password" className="form-control" name="password" minlength="10" required />
                    </div>
                    <button type="submit" id="ss" className="btn btn-primary btn1">Create Account</button>
                </form>
            </div >
        )
    }
}
export default SignUp;
